from . import utl_interface
import logging
import unreal

subobject_system: unreal.SubobjectDataSubsystem = unreal.get_engine_subsystem(unreal.SubobjectDataSubsystem)
class BlueprintInterface(utl_interface.UnrealInterface):

    def __init__(self, logger: logging.Logger = None):
        super().__init__(logger)

    @utl_interface.add_logger
    def find_component_by_name(self, in_actor: unreal.Actor, component_name: str) -> unreal.ActorComponent:
        #with utl_interface.AddObjectScope(bp) as bp_actor:
        components = in_actor.get_components_by_class(unreal.SkeletalMeshComponent)
        for i in components:
            if str(i.get_name()).lower() == component_name.lower():
                return i
        


